// React Component: Header.jsx
import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../styles/Header.scss";
import logo from "../assets/images/Logo.png"; // ajuste le chemin si besoin

export default function Header() {
  const [menuOpen, setMenuOpen] = useState(false);

  return (
    <header className="header">

      {/* LOGO */}
      <a href="#" className="logo">
        <img src={logo} alt="Logo" className="logo-img" />
      </a>

      {/* HAMBURGER */}
      <button
        className={`hamburger ${menuOpen ? "open" : ""}`}
        onClick={() => setMenuOpen(!menuOpen)}
      >
        <span></span>
        <span></span>
        <span></span>
      </button>

      {/* MENU SLIDE */}
      <nav className={`nav-menu ${menuOpen ? "open" : ""}`}>
        <ul className="nav-links">
          <li><a className="nav-button" href="#">Accueil</a></li>
          <li><a className="nav-button" href="/batiment">B√¢timent</a></li>
          <li><a className="nav-button" href="/services">Services</a></li>
          <li><a className="nav-button" href="/fabrication">Fabrication</a></li>
          <li><a className="nav-button" href="/alimentation">Alimentation</a></li>
          <li><a className="nav-button" href="/recherche">Recherche</a></li>
          <li><a className="nav-button" href="/mention">Mention L√©gales</a></li>
        </ul>

      {/* Barre de recherche visible en DESKTOP + TABLETTE */}
      <div className="search-dt">
            <input type="text" className="form-control" placeholder="Rechercher..." />
            <button type="submit">üîç</button>
      </div>

        {/* Recherche mobile */}
        <form className="search-mobile">
          <input type="text" className="form-control" placeholder="Rechercher..." />
          <button type="submit">üîç</button>
        </form>
      </nav>
    </header>
  );
}
