import React, { useState } from 'react';
import '../styles/SearchArtisan.scss';

const SearchArtisan = ({ onSearch, onShowAll }) => {
  const [filters, setFilters] = useState({
    name: '',
    category: '',
    specialty: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFilters((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSearch = () => {
    onSearch(filters);
  };

  const handleShowAll = () => {
    onShowAll();
    setFilters({ name: '', category: '', specialty: '' });
  };

  const handleReset = () => {
    setFilters({ name: '', category: '', specialty: '' });
  };

  return (
    <div className="search-artisan">
      <h2>Rechercher un artisan</h2>
      <div className="form-group">
        <input
          type="text"
          name="name"
          placeholder="Nom de l'artisan"
          value={filters.name}
          onChange={handleInputChange}
        />
        <select
          name="category"
          value={filters.category}
          onChange={handleInputChange}
        >
          <option value="">Sélectionner une catégorie</option>
          <option value="construction">Construction</option>
          <option value="plomberie">Plomberie</option>
          <option value="électricité">Électricité</option>
        </select>
        <select
          name="specialty"
          value={filters.specialty}
          onChange={handleInputChange}
        >
          <option value="">Sélectionner une spécialité</option>
          <option value="renovation">Rénovation</option>
          <option value="reparation">Réparation</option>
        </select>
      </div>
      <div className="button-group">
        <button className="search-btn" onClick={handleSearch}>
          Rechercher
        </button>
        <button className="show-btn" onClick={handleShowAll}>
          Afficher tous
        </button>
        <button className="reset-btn" onClick={handleReset}>
          Réinitialiser
        </button>
      </div>
    </div>
  );
};

export default SearchArtisan;
