import React, { useState } from "react";

const initialState = {
  nom: "",
  email: "",
  objet: "",
  message: "",
};

const initialTouched = {
  nom: false,
  email: false,
  objet: false,
  message: false,
};

function validateField(name, value) {
  switch (name) {
    case "nom":
      if (!value.trim()) return "Le nom est requis.";
      if (value.trim().length < 2) return "Le nom doit comporter au moins 2 caractères.";
      return "";
    case "email":
      if (!value.trim()) return "L'email est requis.";
      // Simple email validation
      // eslint-disable-next-line no-useless-escape
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      if (!emailRegex.test(value.trim())) return "Veuillez saisir une adresse email valide.";
      return "";
    case "objet":
      if (!value.trim()) return "L'objet est requis.";
      if (value.trim().length < 3) return "L'objet doit comporter au moins 3 caractères.";
      return "";
    case "message":
      if (!value.trim()) return "Le message est requis.";
      if (value.trim().length < 10) return "Le message doit comporter au moins 10 caractères.";
      return "";
    default:
      return "";
  }
}

export default function ContactForm({ onBack }) {
  const [values, setValues] = useState(initialState);
  const [touched, setTouched] = useState(initialTouched);
  const [submitted, setSubmitted] = useState(false);
  const [isSending, setIsSending] = useState(false);
  const [serverError, setServerError] = useState("");
  const [serverSuccess, setServerSuccess] = useState("");

  const errors = {
    nom: validateField("nom", values.nom),
    email: validateField("email", values.email),
    objet: validateField("objet", values.objet),
    message: validateField("message", values.message),
  };

  const isValid = Object.values(errors).every((e) => e === "");

  function handleChange(e) {
    const { name, value } = e.target;
    setValues((prev) => ({ ...prev, [name]: value }));
  }

  function handleBlur(e) {
    const { name } = e.target;
    setTouched((prev) => ({ ...prev, [name]: true }));
  }

  async function handleSubmit(e) {
    e.preventDefault();
    setSubmitted(true);
    setServerError("");
    setServerSuccess("");

    // Mark all as touched to show validation messages
    setTouched({
      nom: true,
      email: true,
      objet: true,
      message: true,
    });

    if (!isValid) return;

    try {
      setIsSending(true);
      // Replace with your API call
      await new Promise((res) => setTimeout(res, 800));
      setServerSuccess("Votre message a bien été envoyé.");
      setValues(initialState);
      setTouched(initialTouched);
    } catch (err) {
      setServerError("Une erreur est survenue. Veuillez réessayer.");
    } finally {
      setIsSending(false);
    }
  }

  function fieldState(name) {
    // Returns "default" | "error" | "success"
    const hasValue = values[name]?.trim().length > 0;
    const hasError = errors[name] && (touched[name] || submitted);
    if (hasError) return "error";
    if (hasValue && !errors[name]) return "success";
    return "default";
  }

  return (
    <section className="contact-form">
      <h2 className="contact-form__title">Contactez l’artisan</h2>

      <form onSubmit={handleSubmit} noValidate>
        {/* Nom */}
        <div className={`field field--${fieldState("nom")}`}>
          <label htmlFor="nom" className="field__label">
            Nom*
          </label>
          <div className="field__control">
            <input
              id="nom"
              name="nom"
              type="text"
              className="field__input"
              value={values.nom}
              onChange={handleChange}
              onBlur={handleBlur}
              aria-invalid={errors.nom ? "true" : "false"}
              aria-describedby={errors.nom ? "error-nom" : "hint-nom"}
              placeholder="Votre nom"
            />
            <span className="field__icon" aria-hidden="true" />
          </div>
          {errors.nom && (touched.nom || submitted) ? (
            <p id="error-nom" className="field__message field__message--error">
              {errors.nom}
            </p>
          ) : (
            <p id="hint-nom" className="field__message field__message--hint">
              Saisissez votre nom complet.
            </p>
          )}
          {!errors.nom && values.nom.trim().length > 0 ? (
            <p className="field__message field__message--success">Nom valide.</p>
          ) : null}
        </div>

        {/* Email */}
        <div className={`field field--${fieldState("email")}`}>
          <label htmlFor="email" className="field__label">
            Email*
          </label>
          <div className="field__control">
            <input
              id="email"
              name="email"
              type="email"
              className="field__input"
              value={values.email}
              onChange={handleChange}
              onBlur={handleBlur}
              aria-invalid={errors.email ? "true" : "false"}
              aria-describedby={errors.email ? "error-email" : "hint-email"}
              placeholder="exemple@domaine.fr"
            />
            <span className="field__icon" aria-hidden="true" />
          </div>
          {errors.email && (touched.email || submitted) ? (
            <p id="error-email" className="field__message field__message--error">
              {errors.email}
            </p>
          ) : (
            <p id="hint-email" className="field__message field__message--hint">
              Nous vous contacterons à cette adresse.
            </p>
          )}
          {!errors.email && values.email.trim().length > 0 ? (
            <p className="field__message field__message--success">Email valide.</p>
          ) : null}
        </div>

        {/* Objet */}
        <div className={`field field--${fieldState("objet")}`}>
          <label htmlFor="objet" className="field__label">
            Objet*
          </label>
          <div className="field__control">
            <input
              id="objet"
              name="objet"
              type="text"
              className="field__input"
              value={values.objet}
              onChange={handleChange}
              onBlur={handleBlur}
              aria-invalid={errors.objet ? "true" : "false"}
              aria-describedby={errors.objet ? "error-objet" : "hint-objet"}
              placeholder="Sujet de votre demande"
            />
            <span className="field__icon" aria-hidden="true" />
          </div>
          {errors.objet && (touched.objet || submitted) ? (
            <p id="error-objet" className="field__message field__message--error">
              {errors.objet}
            </p>
          ) : (
            <p id="hint-objet" className="field__message field__message--hint">
              Donnez un titre clair à votre demande.
            </p>
          )}
          {!errors.objet && values.objet.trim().length > 0 ? (
            <p className="field__message field__message--success">Objet valide.</p>
          ) : null}
        </div>

        {/* Message */}
        <div className={`field field--${fieldState("message")}`}>
          <label htmlFor="message" className="field__label">
            Message*
          </label>
          <div className="field__control">
            <textarea
              id="message"
              name="message"
              className="field__input field__textarea"
              rows={6}
              value={values.message}
              onChange={handleChange}
              onBlur={handleBlur}
              aria-invalid={errors.message ? "true" : "false"}
              aria-describedby={errors.message ? "error-message" : "hint-message"}
              placeholder="Décrivez votre besoin"
            />
            <span className="field__icon" aria-hidden="true" />
          </div>
          {errors.message && (touched.message || submitted) ? (
            <p id="error-message" className="field__message field__message--error">
              {errors.message}
            </p>
          ) : (
            <p id="hint-message" className="field__message field__message--hint">
              Ajoutez des détails pour faciliter le traitement.
            </p>
          )}
          {!errors.message && values.message.trim().length > 0 ? (
            <p className="field__message field__message--success">Message valide.</p>
          ) : null}
        </div>

        {/* Legal info */}
        <div className="legal">
          <p className="legal__note">
            Les champs marqués d’un astérisque (*) sont requis.
          </p>
          <p className="legal__note">
            Les informations recueillies à partir de ce formulaire sont nécessaires aux services de la région Auvergne-Rhône-Alpes pour la gestion de votre demande.
          </p>
          <p className="legal__note">
            Pour en savoir plus sur la gestion de vos données et vos droits.
          </p>
        </div>

        {/* Server feedback */}
        {serverError && (
          <div className="form-feedback form-feedback--error" role="alert">
            {serverError}
          </div>
        )}
        {serverSuccess && (
          <div className="form-feedback form-feedback--success" role="status">
            {serverSuccess}
          </div>
        )}

        {/* Actions */}
        <div className="actions">
          <button
            type="button"
            className="btn btn--secondary"
            onClick={onBack}
            aria-label="Retour"
          >
            Retour
          </button>
          <button
            type="submit"
            className="btn btn--primary"
            disabled={isSending}
          >
            {isSending ? "Envoi en cours..." : "Envoyer"}
          </button>
        </div>
      </form>

      {/* Interaction info legend */}
      <aside className="legend" aria-label="Information sur l’interaction">
        <strong className="legend__title">Information sur l’interaction</strong>
        <ul className="legend__list">
          <li><span className="legend__dot legend__dot--default" /> Bleu par défaut</li>
          <li><span className="legend__dot legend__dot--error" /> Rouge erreur</li>
          <li><span className="legend__dot legend__dot--success" /> Vert succès</li>
        </ul>
      </aside>
    </section>
  );
}
