import React from "react";
import "./Header.scss";
import { FiSearch } from "react-icons/fi";
import { FiMenu } from "react-icons/fi";

const categories = ["Accueil", "Bâtiment", "Services", "Fabrication", "Alimentation"];

export default function Header() {
  return (
    <header className="header">
      {/* Logo */}
      <div className="header__brand">
        <div className="title">Trouve ton artisan !</div>
        <div className="subtitle">Avec la région Auvergne-Rhône-Alpes</div>
      </div>

      {/* Categories (desktop) */}
      <nav className="header__nav">
        {categories.map((cat) => (
          <button key={cat} className="nav-btn">
            {cat}
          </button>
        ))}
      </nav>

      {/* Search zone */}
      <div className="header__searchbox">
        <FiMenu className="icon menu" />
        <input
          className="search-input"
          placeholder="Hinted search text"
          aria-label="Recherche"
        />
        <FiSearch className="icon search" />
      </div>
    </header>
  );
}
