import React from 'react';
import '../styles/Footer.scss';
import Logo from '../assets/images/Logo.png';
import AraLogo from '../assets/images/ARA-Logo.png';
import { Link } from 'react-router-dom';

// Exemple d'objets contenant les paths SVG
const svgPaths = {
  p1a0f3740: "M0,30 C300,90 1030,-30 1330,30 L1330,0 L0,0 Z",
  p16f1bd10: "M0,60 C300,120 1030,0 1330,60 L1330,0 L0,0 Z"
};

const Footer = () => {
  return (
    <footer className="footer">
      {/* Wave décorative */}
      <div className="footer-wave">
        <div className="footer-wave-svg">
          <svg fill="none" preserveAspectRatio="none" viewBox="0 0 1330 78">
            <g>
              <path d={svgPaths.p1a0f3740} fill="white" />
              <path d={svgPaths.p16f1bd10} fill="white" />
            </g>
          </svg>
        </div>
      </div>

      {/* Contenu du footer */}
      <div className="footer__content">
        
        {/* Colonne 1 : Image promo */}
        <div className="footer__column footer__promo">
          <img 
            src={Logo} 
            alt="Trouve ton artisan - Région Auvergne-Rhône-Alpes" 
          />
        </div>

        {/* Colonne 2 : Contact */}
        <div className="footer__column">
          <h3>Contact</h3>
          <address>
            <a
              href="https://www.google.com/maps?q=101+cours+Charlemagne,+69269+LYON+CEDEX+02,+France"
              target="_blank"
              rel="noopener noreferrer"
            >
              101 cours Charlemagne<br />
              CS 20033<br />
              69269 LYON CEDEX 02<br />
              France
            </a>
            <br />
            <a href="tel:+33426734000">+33 (0)4 26 73 40 00</a>
          </address>
        </div>

        {/* Colonne 3 : Pages légales */}
        <div className="footer__column">
          <h3>Pages Légales</h3>
          <ul>
            <li><Link to="/mentions-legales">Mentions légales</Link></li>
            <li><Link to="/donnees-personnelles">Données personnelles</Link></li>
            <li><Link to="/accessibilite">Accessibilité</Link></li>
            <li><Link to="/cookies">Cookies</Link></li>
          </ul>
        </div>

        {/* Colonne 4 : Logo Région */}
        <div className="footer__column footer__logo">
          <img src={AraLogo} alt="La Région Auvergne-Rhône-Alpes" />
        </div>
      </div>
    </footer>
  );
};

export default Footer;
