import React from 'react';
import '../styles/ArtisanCard.scss';

function Rating({ rating = 0 }) {
  const stars = Array(5).fill(0).map((_, i) => i < Math.round(rating));

  return (
    <div className="artisan-rating" aria-label={`Note: ${Math.round(rating)} sur 5`} role="img">
      {stars.map((filled, i) => (
        <span key={i} className={`star ${filled ? 'filled' : ''}`}>â˜…</span>
      ))}
    </div>
  );
}

export default function ArtisanCard({
  category,
  name,
  description,
  location,
  rating = 0,
  className = '',
}) {
  return (
    <article className={`artisan-card ${className}`}>
      <div className="artisan-card-top">
        <Rating rating={rating} />
        <div className="artisan-category">{category}</div>
      </div>

      <h3 className="artisan-name">{name}</h3>

      <p className="artisan-description">{description}</p>

      <div className="artisan-location">{location}</div>
    </article>
  );
}
