// Footer.jsx
import React from 'react';
import '../styles/Footer.scss';
import Logo from '../assets/images/Logo.png';
import AraLogo from '../assets/images/ARA-Logo.png';
import { Link } from 'react-router-dom';

const Footer = () => {
  return (
    <footer className="footer">
      <div className="c-footer__wrapper variant3"> 
        {/* üëâ change "variant2" en "variant3" selon le style voulu */}
        <div className="footer__content">
          
          {/* Colonne 1 : Image promo */}
          <div className="footer__column footer__promo">
            <img 
              src={Logo} 
              alt="Trouve ton artisan - R√©gion Auvergne-Rh√¥ne-Alpes" 
            />
          </div>

          {/* Colonne 2 : Contact */}
          <div className="footer__column">
            <h3>Contact</h3>
            <address>
              <a
                href="https://www.google.com/maps?q=101+cours+Charlemagne,+69269+LYON+CEDEX+02,+France"
                target="_blank"
                rel="noopener noreferrer"
              >
                101 cours Charlemagne<br />
                CS 20033<br />
                69269 LYON CEDEX 02<br />
                France
              </a>
              <br />
              <a href="tel:+33426734000">+33 (0)4 26 73 40 00</a>
            </address>
          </div>

          {/* Colonne 3 : Pages l√©gales */}
          <div className="footer__column">
            <h3>Pages L√©gales</h3>
            <ul>
              <li><a href="/mentions-legales">Mentions l√©gales</a></li>
              <li><a href="/donnees-personnelles">Donn√©es personnelles</a></li>
              <li><a href="/accessibilite">Accessibilit√©</a></li>
              <li><a href="/cookies">Cookies</a></li>
            </ul>
          </div>

          {/* Colonne 4 : Logo */}
          <div className="footer__column footer__logo">
            <img src={AraLogo} alt="La R√©gion Auvergne-Rh√¥ne-Alpes" />
          </div>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
